
public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// int data type : where we can store integer data
		
		//Syntax is int a = 10;
		
		int i = 10; // we can not declare same variable to same data type
		i = 20;
		int j= 30;
		int k= 100;
		int m= -1;
		System.out.println(m);
		
		// double data type, it will store decimal numbers
		
		double d = 10.21;
		double d1= 20.12;
		System.out.println(d1);
		
		//char data type: value should be in '';
		
		char a = 'A';
		char b = 'B';
		System.out.println(b);
		
		// String is a  Class not data type
		
		String s = "PepeJeans";
		String s1 = "RockStar";
		String s2 = "Java Practice";
		System.out.println(s2);
		
		// These are all premitive data types
		
		
		

	}

}
